mapper.select:
select PROJECT_NUM, TITLE, W_DESC, FIRST_IMG_FILENAME, STS,
LIQUOR_TYPE, profil_img_filename, USER_NUM
from w_project WHERE
USER_NUM = #{userNum}
and sts=#{sts}

mapper.update:
update w_project
set maker_name = #{makerName},
profil_img_filename = #{profilImgFilename},
maker_desc = #{makerDesc}
WHERE project_num = #{projectNum}

update w_project
set target_amount = #{targetAmount},
start_date = #{startDate},
end_date = #{endDate}
WHERE project_num = #{projectNum}

mapper.select:
SELECT COUNT(*)
FROM w_project
WHERE sts != '종료' AND user_num =#{userNum}
